CREATE TABLE IF NOT EXISTS points_of_interest(
    poi_pid INT AUTO_INCREMENT,
    name VARCHAR(255),
    description TEXT,
    category_id INT,
    location POINT,
    user_id INT,
    PRIMARY KEY(poi_pid),
    FOREIGN KEY (category_id) REFERENCES categories(category_id),
    FOREIGN KEY (user_id) REFERENCES user(user_id)
);

